mapper.select:
SELECT LIST.* , Q.NAME , Q.TYPE , Q.CONTENT , Q.SELECT_TYPE FROM TBL_QUESTION_LIST LIST JOIN TBL_QUESTION Q ON LIST.QUESTION_IDX = Q.IDX
WHERE EXAM_IDX = #{exam_idx}
ORDER BY SEQ

mapper.update:
UPDATE TBL_QUESTION_LIST SET
QUESTION_IDX = #{question_idx},
UPDATE_TM = now() WHERE IDX = #{idx}

mapper.select:
SELECT LIST.* , P.NAME FROM TBL_PRODUCT_LIST LIST JOIN TBL_PRODUCT P ON LIST.PRO_IDX = P.IDX
WHERE EXAM_IDX = #{exam_idx}
ORDER BY MIN_SCORE
